import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import CarList from './carlist';
import ShowRoom from './showroom';

import Pagination from "react-js-pagination";




class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      vehicles : [],
      showVehicals : [],
      activePage : 1,
      perPage : 3,
      pageNums : 1,
    };
    this.fetch = this.fetch.bind(this);
    this.handlePageChange = this.handlePageChange.bind(this);
    this.renderVehicals = this.renderVehicals.bind(this);
    this.paginate = this.paginate.bind(this);
  }

  componentDidMount() {
    this.fetch();
  }

  fetch(){
    axios.get('https://private-4e19e-interviewapi3.apiary-mock.com/vehicles?page=1')
      .then(data => {
        let v = []
        for (var i = 0; i < Object.keys(data.data.data.vehicles).length; i++){
          v.push(data.data.data.vehicles[Object.keys(data.data.data.vehicles)[i]]);
        }
        let tempV = [];
        for (let i = 0; i < 4; i++)
        {
          tempV.push(v[i])
        }
        this.setState({vehicles : v, showVehicals : tempV, pageNums : Math.ceil(v.length/ this.state.perPage)});

        console.log(v, this.state.pageNums)
      })
      .catch(res => {
        if(res instanceof Error) {
          console.log("Error is: ", res.message);
        } else {
          console.log("Other error: ", res.data);
        }
      });

      axios.get('https://private-4e19e-interviewapi3.apiary-mock.com/vehicles/19XFC2F59GE2276732016')
      .then(data => {
        console.log(data);
      })
      .catch(res => {
        if(res instanceof Error) {
          console.log("Error is: ", res.message);
        } else {
          console.log("Other error: ", res.data);
        }
      });
  }

  paginate(){
    let pages = []
    // Outer loop to create parent
    for (let i = 1; i <= this.state.pageNums; i++) {
      let children = []
      pages.push(<li className="paginateNums" onClick={() => {this.handlePageChange(i)}}>{i}</li>)
    }
    return pages

  }

  handlePageChange(i){
    let pageNumber = i;
    let tempV = [];
    for (let i = ((pageNumber - 1)*4); i < ((pageNumber - 1)*4 + 4); i++)
    {
      tempV.push(this.state.vehicles[i])
    }
    console.log(`active page is ${pageNumber}`, tempV);
    this.setState({activePage: pageNumber, showVehicals: tempV});
    this.renderVehicals();

  }

  renderVehicals(){
    return(
      <div>
        {this.state.showVehicals.map((vehicle, index) => {
          return <li key={index}> <CarList vehicle={vehicle}/> </li>;
        })}
      </div>
    )
  }

  render() {
    return (
      <div className="App" >
        <header className="App-header">
          <div className="App-logo" alt="logo">fair</div>
        </header>
        <div id="parentbox">
          <table style={{margin: 'auto'}}>
            <tbody>
              <tr >
                <td id="carlist">
                  <ul id="vlist">
                    {this.renderVehicals()}
                    <div className="paginate">
                      {this.paginate()}
                    </div>
                  </ul>
                </td>
                <td id="showroom">
                    <ShowRoom></ShowRoom>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default App;
