import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import CarList from './carlist.jsx';
import ShowRoom from './showroom.jsx';
import Pagination from "react-js-pagination";


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      vehicles : [],
      showVehicals : [],
      activePage : 1,
    };
    this.fetch = this.fetch.bind(this);
    this.handlePageChange = this.handlePageChange.bind(this);
    this.renderVehicals = this.renderVehicals.bind(this);
  }

  componentDidMount() {
    this.fetch();
  }

  fetch(){
    axios.get('https://private-4e19e-interviewapi3.apiary-mock.com/vehicles?page=1')
      .then(data => {
        let v = []
        for (var i = 0; i < Object.keys(data.data.data.vehicles).length; i++){
          v.push(data.data.data.vehicles[Object.keys(data.data.data.vehicles)[i]]);
        }
        let tempV = [];
        for (let i = 0; i < 5; i++)
        {
          tempV.push(v[i])
        }
        this.setState({vehicles : v, showVehicals: tempV});
        console.log(v)
      })
      .catch(res => {
        if(res instanceof Error) {
          console.log("Error is: ", res.message);
        } else {
          console.log("Other error: ", res.data);
        }
      });

      axios.get('https://private-4e19e-interviewapi3.apiary-mock.com/vehicles/19XFC2F59GE2276732016')
      .then(data => {
        console.log(data);
      })
      .catch(res => {
        if(res instanceof Error) {
          console.log("Error is: ", res.message);
        } else {
          console.log("Other error: ", res.data);
        }
      });
  }

  handlePageChange(){
    let pageNumber = arguments[0];
    let tempV = [];
    for (let i = ((pageNumber - 1)*5); i < ((pageNumber - 1)*5 + 5); i++)
    {
      tempV.push(this.state.vehicles[i])
    }
    console.log(`active page is ${pageNumber}`, tempV);
    this.setState({activePage: pageNumber, showVehicals: tempV});
    this.renderVehicals();

  }

  renderVehicals(){
    return(
      <div>
        {this.state.showVehicals.map((vehicle, index) => {
          return <li key={index}> <CarList vehicle={vehicle}/> </li>;
        })}
      </div>
    )
  }

  render() {
    return (
      <div>                  <Pagination id="paginate"
      activePage={this.state.activePage}
      itemsCountPerPage={5}
      totalItemsCount={this.state.vehicles.length}
      pageRangeDisplayed={5}
      onChange={this.handlePageChange}
    /> </div>
    );
  }
}

export default App;
