import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import CarList from './carlist.jsx';
import ShowRoom from './showroom.jsx';
import Pagination from "react-js-pagination";


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      vehicles : [],
      showVehicals : [],
      activePage : 1,
    };
    this.fetch = this.fetch.bind(this);
    this.handlePageChange = this.handlePageChange.bind(this);
  }

  componentDidMount() {
    this.fetch();
  }

  fetch(){
    axios.get('https://private-4e19e-interviewapi3.apiary-mock.com/vehicles?page=1')
      .then(data => {
        let v = []
        for (var i = 0; i < Object.keys(data.data.data.vehicles).length; i++){
          v.push(data.data.data.vehicles[Object.keys(data.data.data.vehicles)[i]]);
        }
        this.setState({vehicles : v});
        console.log(v)
      })
      .catch(res => {
        if(res instanceof Error) {
          console.log("Error is: ", res.message);
        } else {
          console.log("Other error: ", res.data);
        }
      });

      axios.get('https://private-4e19e-interviewapi3.apiary-mock.com/vehicles/19XFC2F59GE2276732016')
      .then(data => {
        console.log(data);
      })
      .catch(res => {
        if(res instanceof Error) {
          console.log("Error is: ", res.message);
        } else {
          console.log("Other error: ", res.data);
        }
      });
  }

  handlePageChange(){
    let pageNumber = arguments[0];
    let tempV = [];
    for (var i = ((pageNumber - 1)*5); i < ((pageNumber - 1)*5 + 5); i++)
    {}
    console.log(`active page is ${pageNumber}`);
    this.setState({activePage: pageNumber, showVehicals:[]});

  }

  renderVehicals(){

    return(
      <div>
        {this.state.vehicles.map((vehicle, index) => {
          return <li key={index}> <CarList vehicle={vehicle}/> </li>;
        })}
      </div>
    )
  }

  render() {
    return (
      <div className="App" >
        <header className="App-header">
          <div className="App-logo" alt="logo">fair</div>
        </header>
        <div id="parentbox">
          <table style={{margin: 'auto'}}>
            <tbody>
              <tr >
                <td id="carlist">
                  <ul id="vlist">
                    {this.renderVehicals()}
                  </ul>
                  <Pagination id="paginate"
                    activePage={this.state.activePage}
                    itemsCountPerPage={5}
                    totalItemsCount={this.state.vehicles.length}
                    pageRangeDisplayed={5}
                    onChange={this.handlePageChange}
                  /> 
                </td>
                <td id="showroom">
                    <ShowRoom></ShowRoom>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default App;
