import React, { Component } from 'react';
import axios from 'axios';
import './App.css';

class CarList extends Component {
  constructor(props) {
    super(props)
    this.state = {
      vehicle : props.vehicle,
    };
    this.fetch = this.fetch.bind(this);
  }

  componentDidMount() {
  }

  fetch(){
    axios.get('https://private-4e19e-interviewapi3.apiary-mock.com/vehicles?page=1')
      .then(data => {
        let v = data.data.data.vehicles;
        this.setState({vehicles : v});
        console.log(v)
      })
      .catch(res => {
        if(res instanceof Error) {
          console.log("Error is: ", res.message);
        } else {
          console.log("Other error: ", res.data);
        }
      });
  }

  handleclick(){
    // need to send up to favorites
    return "checkmark";
  }

  render() {
    return (
        <div className="vehicalbox">
            <ul>
                <label className="container" id="box">
                    <input type="checkbox" onClick={() => {this.handleclick()}}/>
                    <span className="checkmark">   Favorite</span>
                </label>
                <img src={this.state.vehicle.chrome_image_url} id="carpic"></img>
                <li id="description" style={{textAlign: "left"}}>
                    <div id="info">
                        <div>{this.state.vehicle.model_year} {this.state.vehicle.make} {this.state.vehicle.model}</div>
                        <div>Trim: {this.state.vehicle.trim}</div>
                        <div>Miles: {this.state.vehicle.mileage}</div>
                    </div>
                </li>
                <li id="description" style={{textAlign: "left"}}>
                  <div id="price">
                        <div>Start Fee: <span style={{color: '#FF651B'}}>{this.state.vehicle.product_financials[0].start_fee_cents / 100}</span></div>
                        <div>Monthly Fee: <span style={{color: '#FF651B'}}>{this.state.vehicle.product_financials[0].monthly_payment_cents / 100}</span></div>
                  </div>
                </li>
            </ul>
        </div>
    );
  }
}

export default CarList;
